        this.setState({
          products: [...this.state.products, product]
        })
      }
      this.setState({ loading: false})
    } else {
      window.alert('Marketplace contract not deployed to detected network.')
    }
  }

  render() {
    return (
      <div className="Home">
        <Nbar/>
      </div>
    );
  }
}

export default App;
