import React, { Component } from 'react';
import web3 from './web3.js'
import Nbar from './Nbar.js';
import storage from './storage.js'

class Create extends Component {

  constructor(props){
    super(props)
    this.state = {
      account: '',
      num:5,
      submitNum:0
    }    
    this.handleName = this.handleName.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleName(e) {
    this.setState({num: e.target.value});
  }

  async handleClick(e) {
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    this.setState({submitNum:this.state.num})
    await storage.methods.store(parseInt(this.state.submitNum)).send({ from: this.state.account })
    console.log(this.state.submitNum);
  }

  render() {
    const styleInput={
      border:'2px solid'
    };
    return (
      <div>
        <Nbar account={this.state.account}/>
        <form>
          <label>
            Name
            <input type="text" placeholder="name" style={styleInput} onChange={ this.handleName } />
          </label>
          <br/>
          <label>
            <input type="text" placeholder="phone" style={styleInput} onChange={ this.handleName } />
          </label>
          <br/>
          <label>
            <input type="text" placeholder="email" style={styleInput} onChange={ this.handleName } />
          </label>
          <br/>
          <label>
            <input type="text" placeholder="address" style={styleInput} onChange={ this.handleName } />
          </label>
          <br/>
          <label>
            <input
              type="button"
              value="confirm"
              style={{cursor:'pointer'}}
              onClick={this.handleClick}
            />
          </label>
        </form>
      </div>
    );
  }
}


export default Create;
