import React, { Component } from 'react';
import Storage from '../abis/Storage.json'
import Web3 from 'web3'
import Nbar from './Nbar.js';

class Create extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Storage.networks[networkId]
    if(networkData) {
      const storage = web3.eth.Contract(Storage.abi, networkData.address)
      console.log(Storage.abi, networkData.address)
      this.setState({ storage })
    } else {
      window.alert('Marketplace contract not deployed to detected network.')
    }
  }

  constructor(props){
    super(props)
    this.state = {
      account: '',
      num:5
    }    
  }
  setNum(){
    this.state.storage.methods.store(10,{ from: this.state.account }).call()
    this.setState({num:123});
  }
  render() {
    return (
      <div>
        <Nbar account={this.state.account}/>
        <h1>
          create member
        </h1>
        <div>
          <input type="text"/>
          <button onClick={()=>{this.setNum()}}>用按鍵取得新的num</button>
        </div>
        <a>{this.state.num}</a>
      </div>
    );
  }
}


export default Create;
